const lastPage = 3 // change this according to the number of pages
let currentPage = 1

const setVisibility = (selector, isVisible) => {
  // Extract the content from the current element
  const target = this.options.el.querySelector(selector)

  // Change the visibility property for the footer,
  // but switch the display setting for the screen content
  if (['#bck', '#done', '#fwd'].includes(selector)) {
    target.style.visibility = isVisible ? 'visible' : 'hidden'
  } else {
    target.style.display = isVisible ? 'initial' : 'none'
  }
}

// Move to the next page
this.options.events['keydown(ArrowRight)'] = function() {

  // make sure that 'back' button is visible
  setVisibility('#bck', true)

  if (currentPage < lastPage) {
    // hide current page and header
    setVisibility('#page' + currentPage, false)
    setVisibility('#head' + currentPage, false)

    currentPage +=1

    // show next page and header
    setVisibility('#page' + currentPage, true)
    setVisibility('#head' + currentPage, true)
  }

  if (currentPage == lastPage) {
    // hide 'back' button if beginning is reached
    setVisibility('#fwd', false)

    // and show 'end' button
    setVisibility('#done', true)
  }
}

// Move to the previous page
this.options.events['keydown(ArrowLeft)'] = function() {

  // make sure that 'continue' button is visible
  setVisibility('#fwd', true)
  
  // and hide 'end' button if it was shown
  setVisibility('#done', false)

  if (currentPage > 1) {
    // hide current page and header
    setVisibility('#page' + currentPage, false)
    setVisibility('#head' + currentPage, false)

    currentPage -=1

    // show previous page and header
    setVisibility('#page' + currentPage, true)
    setVisibility('#head' + currentPage, true)
  }

  // hide 'back' button if beginning is reached
  if (currentPage == 1) {
    setVisibility('#bck', false)
  }
}

this.options.events['keydown(Space)'] = function() {
  if (currentPage == lastPage) {
    // End instructions
    this.end('done')
  }
}
